// https://github.com/robert-carroll/ccsd-canvas - admin course links
(function(){'use strict';onPage(/^\/accounts\/\d+$/,function(){var links={'users':'People','grades':'Grades','settings':'Settings',};if(Object.keys(links).length<=0)return;const el='tbody[data-automation="courses list"] tr td:nth-child(2)';const mark='rc-course-links';const courses=()=>{let targets=document.querySelectorAll(`${el} > a`),count=targets.length;let marked=document.querySelectorAll(`${el}  > a + .${mark}`);let child,parent;if(targets.length>marked.length){for(let i=0;i<count;i++){if(!targets[i].querySelector('.'+mark)){child=addLinks(targets[i].href);parent=targets[i].parentNode;if(parent&&child){parent.appendChild(child)}}}}};const addLinks=(base)=>{let div=document.createElement('div');div.classList.add(mark);let ul=document.createElement('ul'),item,a;Object.keys(links).forEach(function(key){item=document.createElement('li');a=document.createElement('a');a.href=`${base}/${key}`;a.textContent=links[key];item.appendChild(a);ul.appendChild(item)});div.append(ul);return div};const watch=document.getElementById('content');const observer=new MutationObserver(courses);observer.observe(watch,{childList:!0,subtree:!0});courses()})})()